import GeneralHeader from '@src/components/header/general-header.vue';

@Component({
	components: {
		GeneralHeader
	}
})
export default class Register extends Vue {
	nickname: string = '';
	name: string = '';
	password: string = '';
	sex: string = '';
	age: string = '';
	uploadImg: any = '';
	headerImg: any = '';
	button: MyButton.Button<MyButton.BtnStyle> = {
		disabled: false,
		value: '注册',
		btnStyle: {
			width: '7.75rem',
			height: '1.175rem',
			fontSize: '0.5rem'
		}
	};

	public get api() {
		return this.$store.user.api;
	}

	async register() {
		let params = {
			nickname: this.nickname,
			username: this.name,
			password: this.password,
			sex: this.sex,
			age: this.age,
			headimg: '1'
		};
		params.headimg = this.headerImg;
		this.button.disabled = true;
		const r = await this.api.userRegister(params);
		// closeLoading();
		setTimeout(() => {
			this.button.disabled = false;
		}, 1000);
		if (r.code !== 0) return;
		return this.$router.push({
			name: 'login',
			query: {
				nickname: params.nickname as any
			}
		});
	}
	clickUpload() {
		(this as any).$refs.uploadFile.click();
	}
	upload(e: any) {
		const { headerImg } = this;
		const file = e.target.files[0];
		const reader = new FileReader();
		reader.readAsDataURL(file);
		reader.onload = () => {
			this.headerImg = reader.result;
		};
	}
}

