export const formatDateToStr = (time: number, formatStr: string) => {
	const date = new Date();
	date.setTime(time);
	return formatStr
		.replace(/yyyy|YYYY/, String(date.getFullYear()))
		.replace(/yy|YY/, String(date.getFullYear()))
