		const params = {
			nickname
		};
		const r = await this.loginModule.api.getUserHeaderImg(params);
		if (r.code !== 0 || !r.data) return this;
		this.url = r.data.headimg;
		return this;
	}
	async login() {
		const { nickname } = this;
