		setTimeout(() => {
			this.button.disabled = false;
		}, 1000);
		// if (this.loginModule.res.code !== 0)
		// 	return (this as any).$toast(this.loginModule.res.data);
		const to = this.from ? this.from : '/';
		return this.$router.push({ path: to });
	}
	close() {
		return this.$router.go(-1);
	}
}

