import { Vue, Component } from 'vue-property-decorator';

@Component<Reset>({})
export default class Reset extends Vue {
	nickname: string = '';
	name: string = '';
	sex: string = '';
	age: string = '';
	password: string = '';
	button: MyButton.Button<MyButton.BtnStyle> = {
		disabled: false,
		value: '重置',
		btnStyle: {
			width: '7.75rem',
			height: '1.175rem',
			fontSize: '0.5rem'
		}
	};

	get resetModule() {
		return this.$store.user.userReset;
	}

	async reset() {
		let params = {
			nickname: this.nickname,
			username: this.name,
			sex: this.sex,
			age: this.age,
			password: this.password
		};
		if ((this as any).isEmpty(params)) {
			console.log('请填写完整信息');
			return;
		}
		// if (this.isEmpty) return Toast('', '请按要求填写!');
		this.button.disabled = true;
		this.resetModule.$assignParams(params);
		await this.resetModule.userReset();
		this.button.disabled = false;
		// Toast('', this.resetModule.res.data);
		if (this.resetModule.res.code !== 0) return;
		return this.$router.push({
			name: 'login',
			query: {
				nickname: params.nickname as any
			}
		});
	}
}

